# Functie om een vraag te stellen en een geldig antwoord te krijgen
def ask_question(question, options):
    while True:
        print(question)
        for option in options:
            print(option)
        answer = input().lower()
        if answer in options:
            return answer
        else:
            print("Ongeldige invoer. Probeer opnieuw.")

# Functie om de beste bestemming te vinden op basis van gebruikersinvoer
def find_best_destination(user_answers, destinations):
    best_destination = None
    best_score = -1

    for destination, properties in destinations.items():
        score = 0
        for key, value in user_answers.items():
            if key in properties and properties[key] == value:
                score += 1
        if score > best_score:
            best_score = score
            best_destination = destination

    return best_destination

# Hoofdprogramma
def main():
    # Stel de gebruiker vragen om hun voorkeuren te achterhalen
    user_answers = {}
    user_answers['temperature'] = ask_question("Houdt u van een warme of koude temperatuur?", ["warm", "koud"])
    user_answers['mountain'] = ask_question("Houdt u van bergachtig gebied?", ["ja", "nee"])
    user_answers['beach'] = ask_question("Houdt u van een strand?", ["ja", "nee"]) 
    user_answers['activity'] = ask_question("Houdt u van een actieve of relaxed vakantie?", ["actief", "relaxed"]) 
    user_answers['nature'] = ask_question("Houdt u van natuur of houdt u meer van stedelijk gebied?", ["natuur", "stad"]) 
    user_answers['modern'] = ask_question("Houdt u van een moderne of authentieke bestemming?", ["modern", "authentiek"]) 
    user_answers['destination'] = ask_question("Wilt u in Europa of wereldwijd op vakantie?", ["europa", "wereldwijd"]) 
    user_answers['religion'] = ask_question("Heeft u voorkeur voor geloofsgebieden?", ["ja", "nee"]) 
    if user_answers['religion'] == "ja": 
        user_answers['religion_choice'] = input("Heeft u voorkeur voor geloofsgebieden (zo ja voor welk geloof: Christendom, Islam, Boedisme, Hindoeisme) ").lower()
    user_answers['vehicle'] = ask_question("Met welk voertuig moet je ernaartoe kunnen? (bijv. auto, vliegtuig)", ["auto", "vliegtuig"]) 
    user_answers['tourist_area'] = ask_question("Wilt u een toeristisch gebied?", ["ja", "nee"]) 
    user_answers['same_culture'] = ask_question("Wilt u dezelfde cultuurzone als uw huidige cultuurzone?", ["ja", "nee"]) 
    user_answers['remote_destination'] = ask_question("Wilt u in een afgelegen bestemming verblijven?", ["ja", "nee"]) 
    user_answers['purpose'] = ask_question("Komt u voor de cultuur of voor de frisse lucht?", ["cultuur", "frisse lucht"]) 

    # Toon de antwoorden van de gebruiker
    print("\nUw antwoorden:")
    for key, value in user_answers.items():
        print(f"{key}: {value}")

    # Zoek de beste bestemming op basis van de antwoorden van de gebruiker
    best_destination = find_best_destination(user_answers, plaatsen)

    # Toon de beste bestemming aan de gebruiker
    print(f"\nMeest overeenkomende uiteindelijke bestemming: {best_destination}")


if __name__ == "__main__":
    main()

